
// err_util.js
//
// Packt Publishing - Build a Network Application with Node
// Report an error response with the appropriate status code

exports.report = function(err, res, statusCode, msg) {
  if (err) console.log(err);
  res.writeHead(statusCode || 500, { "Content-Type": "text/plain" });
  res.end(msg || "Internal Server Error");
};
